DROP TABLE IF EXISTS `clientes`;
CREATE TABLE `clientes` (
  `id` smallint(6) NOT  default '0',
  `nome_empresa` varchar(20) default 
  `autarquia` varchar(34) default 
  `nome_orgao_resp` varchar(25) default 
  `logradouro` varchar(34) default 
  `bairro` varchar(20) default 
  `cidade` varchar(20) default 
  `uf` char(2) default 
  `cep` varchar(10) default 
  `fone` varchar(11) default 
  `cgc` varchar(18) default 
  `data_implant_sistema` datetime default 
  `ident_empresa` varchar(4) default 
  PRIMARY KEY  (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

  
